import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookmarkForm from './components/BookmarkForm';
import BookmarkList from './components/BookmarkList';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
    const [bookmarks, setBookmarks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editingBookmark, setEditingBookmark] = useState(null);

    useEffect(() => {
        fetchBookmarks();
    }, []);

    const fetchBookmarks = async () => {
        try {
            const response = await axios.get(`${API_URL}/bookmarks`);
            setBookmarks(response.data.data);
        } catch (error) {
            console.error('Error fetching bookmarks:', error);
        } finally {
            setLoading(false);
        }
    };

    const addBookmark = async (bookmark) => {
        try {
            const response = await axios.post(`${API_URL}/bookmarks`, bookmark);
            setBookmarks([response.data.data, ...bookmarks]);
            return { success: true };
        } catch (error) {
            return { 
                success: false, 
                error: error.response?.data?.error || 'Failed to add bookmark' 
            };
        }
    };

    const updateBookmark = async (id, bookmark) => {
        try {
            const response = await axios.put(`${API_URL}/bookmarks/${id}`, bookmark);
            setBookmarks(bookmarks.map(b => b._id === id ? response.data.data : b));
            setEditingBookmark(null);
            return { success: true };
        } catch (error) {
            return { 
                success: false, 
                error: error.response?.data?.error || 'Failed to update bookmark' 
            };
        }
    };

    const deleteBookmark = async (id) => {
        if (window.confirm('Are you sure you want to delete this bookmark?')) {
            try {
                await axios.delete(`${API_URL}/bookmarks/${id}`);
                setBookmarks(bookmarks.filter(b => b._id !== id));
            } catch (error) {
                alert('Failed to delete bookmark');
            }
        }
    };

    return (
        <div className="app">
            <header className="header">
                <div className="container">
                    <h1>
                        <i className="fas fa-bookmark"></i>
                        Bookmark Manager
                    </h1>
                    <p>Save and organize your favorite websites</p>
                </div>
            </header>

            <main className="container">
                <BookmarkForm 
                    onSubmit={editingBookmark ? 
                        (data) => updateBookmark(editingBookmark._id, data) : 
                        addBookmark
                    }
                    initialData={editingBookmark}
                    onCancel={() => setEditingBookmark(null)}
                />

                {loading ? (
                    <div className="loading">Loading...</div>
                ) : (
                    <BookmarkList 
                        bookmarks={bookmarks}
                        onDelete={deleteBookmark}
                        onEdit={setEditingBookmark}
                    />
                )}
            </main>
        </div>
    );
}

export default App;
